void copyRequestObjectParameters(def request, def requestJwt, def paramName)
{
  def delimiter = '?';
  if(request?.uri.contains('?'))
  	delimiter = '&';

  if(getJwtTokenClaim(requestJwt, paramName) != null)
  {
    def newUri = request?.uri.replaceAll(paramName + "=", paramName + "_old=");    
    request?.setUri(newUri + delimiter + paramName + "=" + getJwtTokenClaim(requestJwt, paramName));
  }
}

